(window.webpackJsonp=window.webpackJsonp||[]).push([[13],{337:function(t,e,v){"use strict";v.r(e);var _=v(0),r=Object(_.a)({},(function(){var t=this,e=t.$createElement,v=t._self._c||e;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h1",{attrs:{id:"《活山》读后"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#《活山》读后"}},[t._v("#")]),t._v(" 《活山》读后")]),t._v(" "),v("p",[t._v("2019年年末读完的这本书，算是一年之内难得读的一本自然文学作品，讲述作者娜恩·谢泼德一生在山中所见所感。书很薄，翻译成中文后只有小小的一本，从图书馆借来的时候还以为是游记类作品，起初翻看的时候并没有读下去，搁在书桌上半个月年会往增城去的路上无聊我才拿在了手上，然而意外的在颠簸的大巴里我飞快地翻看了半本还意犹未尽。")]),t._v(" "),v("p",[t._v("或许是字里行间的自然和灵动引人入胜，或许是描述景色美丽而高远，高耸的群山寒冷的冰雪，还有登山时的快乐都令人兴奋。我也大言不惭地说自个是个爱山的人，喜欢登高望远俯视天地的快乐，若是说起对山的感知对一草一木的观察，那万万是不及的。只记得脚步踩在山石之下，呼吸间萦绕的是清冷的气息，分不清拂过脸庞的是雾还是风，抬头仰望时一眼望不到头的神秘感。")]),t._v(" "),v("p",[t._v("曾几何时我以为到了山顶就是征服，等同于爬完了这座山。可读这本书的时候，我恍然发现自己似乎只是个过客，匆匆在山林间留下个身影又匆匆离开了。山依旧在那里，春夏秋冬风景各异，风霜雪雨我都未曾见过，何曾说来的爱山呢？")]),t._v(" "),v("p",[t._v("2019/12/27, 15:21")]),t._v(" "),v("hr"),t._v(" "),v("p",[t._v("读完这本书感触最大的就是作者对周遭的观察和体验，只有接触过大山接触过自然的人，才会写出真正自然的文字吧，很喜欢后面对动物植物的描写，灵动活泼极了，很难想象朴素的文字下面居然能描绘出那样生动的片段。")]),t._v(" "),v("p",[t._v("偶然看到了一部电影《海蒂和爷爷》，影片中阿尔卑斯山的美景令人神往，也总算能体会作者笔下凯恩戈姆山脉会是何等的美丽。")]),t._v(" "),v("p",[t._v("2020/1/6, 15:24")])])}),[],!1,null,null,null);e.default=r.exports}}]);